import React, { useEffect, useState } from 'react';
import './.css';

const Post = ({ item, index, currentVisibleIndex }) => {
  const [isPause, setIsPause] = useState(true);

  useEffect(() => {
    if (index === currentVisibleIndex && !isPause) {
      setIsPause(true);
    }
  }, [currentVisibleIndex]);

  return (
    <div className="post-container" onClick={() => setIsPause(!isPause)}>
      <video
        src={item.video_url}
        className="post-video"
        onError={(e) => console.log(e)}
        controls
        autoPlay
        muted
        loop
        style={{
          display: index === currentVisibleIndex ? 'block' : 'none',
          borderRadius: '10px'
        }}
        paused={index !== currentVisibleIndex || isPause}
      />

      {!isPause && (
        <div className="play-icon">
          <i className="icon ion-ios-play" style={{ opacity: 0.8 }} />
        </div>
      )}

      <div className="post-info">
        <div className="post-info-top">
          <h4>{item.channel_name}</h4>
          <p>{item.caption}</p>
          <div className="music-info">
            <i className="icon ion-ios-musical-notes" />
            <p>{item.music_name}</p>
          </div>
        </div>

        <div className="post-info-bottom">
          <div className="post-interactions">
            <div className="interaction-item">
              <i className="icon ion-ios-heart" />
              <p>100</p>
            </div>
            <div className="interaction-item">
              <i className="icon ion-ios-chatbubbles" />
              <p>200</p>
            </div>
            <div className="interaction-item">
              <i className="icon ion-ios-bookmark" />
              <p>300</p>
            </div>
            <div className="interaction-item">
              <i className="icon ion-ios-share" />
              <p>400</p>
            </div>
          </div>
          <div className="post-avatar">
            <img
              src="https://placehold.jp/40/FFDF48/000000/150x150.png?text=Full%0AStack"
              alt="avatar"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Post;
