import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [videos, setVideos] = useState([]);
  const [agreed, setAgreed] = useState(false);
  const containerRef = useRef(null);

  useEffect(() => {
    axios.get('http://localhost:5000/videos')
      .then(response => {
        setVideos(response.data);
      })
      .catch(error => {
        console.error('Error fetching videos:', error);
      });
  }, []);

  const handleAgree = () => {
    setAgreed(true);
  };

  if (!agreed) {
    return (
      <div className="agree-screen">
        <h1>Welcome to Fanztik</h1>
        <p>Please agree to our terms to watch the videos.</p>
        <button onClick={handleAgree}>I Agree</button>
      </div>
    );
  }

  if (videos.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div className="App" ref={containerRef} onScroll={handleScroll}>
      <h1>Fanztik Videos</h1>
      <div className="video-container">
        {videos.map((video, index) => (
          <div key={index} className="video-card">
            <h2>{video.title}</h2>
            <video controls autoPlayz src={video.url} />
            <p>{video.description}</p>
          </div>
        ))}
      </div>
    </div>
  );

  function handleScroll() {
    const container = containerRef.current;
    if (container) {
      const { scrollTop, scrollHeight, clientHeight } = container;
      if (scrollTop + clientHeight >= scrollHeight) {
        setVideos((prevVideos) => {
          const nextIndex = prevVideos.length;
          const newVideos = [...prevVideos];
          if (nextIndex < videos.length) {
            newVideos.push(videos[nextIndex % videos.length]);
          }
          return newVideos;
        });
      }
    }
  }
}

export default App;
