// src/App.js
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [videos, setVideos] = useState([]);
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);
  const [agreed, setAgreed] = useState(false);
  const videoRef = useRef(null);

  useEffect(() => {
    axios.get('http://localhost:5000/videos')
      .then(response => {
        setVideos(response.data);
      })
      .catch(error => {
        console.error('Error fetching videos:', error);
      });
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting) {
          setCurrentVideoIndex((prevIndex) => (prevIndex + 1) % videos.length);
        }
      },
      { threshold: 0.5 }
    );

    if (videoRef.current) {
      observer.observe(videoRef.current);
    }

    return () => {
      if (videoRef.current) {
        observer.unobserve(videoRef.current);
      }
    };
  }, [videos]);

  const handleAgree = () => {
    setAgreed(true);
  };

  if (!agreed) {
    return (
      <div className="agree-screen">
        <h1>Welcome to Fanztik</h1>
        <p>Please agree to our terms to watch the videos.</p>
        <button onClick={handleAgree}>I Agree</button>
      </div>
    );
  }

  if (videos.length === 0) {
    return <div>Loading...</div>;
  }

  const currentVideo = videos[currentVideoIndex];

  return (
    <div className="App">
      <h1>Fanztik Videos</h1>
      <div className="video-container">
        <div ref={videoRef} className="video-card">
          <h2>{currentVideo.title}</h2>
          <video controls autoPlay src={currentVideo.url} />
          <p>{currentVideo.description}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
