import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import './App.css';
import Post from './components/video'; // インポート文を追加

function App() {
  const [videos, setVideos] = useState([]);
  const [agreed, setAgreed] = useState(false);
  const videoRefs = useRef([]);
  const [activeVideoIndex, setActiveVideoIndex] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/videos')
      .then(response => {
        setVideos(response.data);
      })
      .catch(error => {
        console.error('Error fetching videos:', error);
      });
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        let maxRatio = 0;
        let indexToPlay = null;

        entries.forEach((entry) => {
          const ratio = entry.intersectionRatio;
          const index = entry.target.dataset.index;
          if (ratio > maxRatio) {
            maxRatio = ratio;
            indexToPlay = parseInt(index);
          }
        });

        setActiveVideoIndex(indexToPlay);
      },
      { threshold: Array.from({ length: 101 }, (_, i) => i / 100) }
    );

    if (videoRefs.current.length) {
      videoRefs.current.forEach(ref => {
        if (ref) observer.observe(ref);
      });
    }

    return () => {
      if (videoRefs.current.length) {
        videoRefs.current.forEach(ref => {
          if (ref) observer.unobserve(ref);
        });
      }
    };
  }, [videos]);

  useEffect(() => {
    if (activeVideoIndex !== null) {
      videoRefs.current.forEach((ref, index) => {
        const video = ref.querySelector('video');
        if (index === activeVideoIndex) {
          video.play();
        } else {
          video.pause();
          video.currentTime = 0; // Reset the video to the beginning
          // Clear the video source to clear the cache
          video.src = video.src;
        }
      });
    }
  }, [activeVideoIndex]);

  const handleAgree = () => {
    setAgreed(true);
  };

  if (!agreed) {
    return (
      <div className="agree-screen">
        <h1>Welcome to Fanztik</h1>
        <p>Please agree to our terms to watch the videos.</p>
        <button onClick={handleAgree}>I Agree</button>
      </div>
    );
  }

  if (videos.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div className="App">
      <h1>Fanztik Videos</h1>
      <div className="video-container">
        {videos.map((video, index) => (
          <div
            key={index}
            ref={el => (videoRefs.current[index] = el)}
            data-index={index}
            className="video-card"
          >
            <Post item={video} index={index} currentVisibleIndex={activeVideoIndex} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
