// src/App.js
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [videos, setVideos] = useState([]);
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);
  const [agreed, setAgreed] = useState(false);
  const videoRefs = useRef([]);

  useEffect(() => {
    axios.get('http://localhost:5000/videos')
      .then(response => {
        setVideos(response.data);
      })
      .catch(error => {
        console.error('Error fetching videos:', error);
      });
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            setCurrentVideoIndex((prevIndex) => {
              const newIndex = prevIndex + 1;
              if (newIndex >= videos.length) {
                return 0;
              }
              return newIndex;
            });
          }
        });
      },
      { threshold: 1.0 }
    );

    if (videoRefs.current.length) {
      videoRefs.current.forEach(ref => {
        if (ref) observer.observe(ref);
      });
    }

    return () => {
      if (videoRefs.current.length) {
        videoRefs.current.forEach(ref => {
          if (ref) observer.unobserve(ref);
        });
      }
    };
  }, [videos]);

  const handleAgree = () => {
    setAgreed(true);
  };

  if (!agreed) {
    return (
      <div className="agree-screen">
        <h1>Welcome to Fanztik</h1>
        <p>Please agree to our terms to watch the videos.</p>
        <button onClick={handleAgree}>I Agree</button>
      </div>
    );
  }

  if (videos.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <div className="App">
      <h1>Fanztik Videos</h1>
      <div className="video-container">
        {videos.map((video, index) => (
          <div
            key={index}
            ref={el => (videoRefs.current[index] = el)}
            className="video-card"
            style={{ display: index === currentVideoIndex ? 'block' : 'none' }}
          >
            <h2>{video.title}</h2>
            <video controls autoPlay src={video.url} />
            <p>{video.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
