// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [videos, setVideos] = useState([]);
  const [currentVideoIndex, setCurrentVideoIndex] = useState(0);
  const [agreed, setAgreed] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:5000/videos')
      .then(response => {
        setVideos(response.data);
      })
      .catch(error => {
        console.error('Error fetching videos:', error);
      });
  }, []);

  const handleNextVideo = () => {
    setCurrentVideoIndex((prevIndex) => (prevIndex + 1) % videos.length);
  };

  const handleAgree = () => {
    setAgreed(true);
  };

  if (!agreed) {
    return (
      <div className="agree-screen">
        <h1>Welcome to Fanztik</h1>
        <p>Please agree to our terms to watch the videos.</p>
        <button onClick={handleAgree}>I Agree</button>
      </div>
    );
  }

  if (videos.length === 0) {
    return <div>Loading...</div>;
  }

  const currentVideo = videos[currentVideoIndex];

  return (
    <div className="App">
      <h1>Fanztik Videos</h1>
      <div className="video-card">
        <h2>{currentVideo.title}</h2>
        <video controls autoPlay src={currentVideo.url} />
        <p>{currentVideo.description}</p>
        <button onClick={handleNextVideo}>Next Video</button>
      </div>
    </div>
  );
}

export default App;
